sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }

    //Type appropriate comment here, and begin script below
    var caseSummary = g_form.getValue('short_description');
    var ga = new GlideAjax('CustPredictAjaxUtils'); // getServiceInfo is the script include name 
    ga.addParam('sysparm_name', 'getModel');
    ga.addParam('sysparm_sdesc', caseSummary); // set service

    /* Call GetUserInfo.managerName() with user set to Fred Luddy and use the callback function ManagerParse() to return the result when ready */
    ga.getXMLAnswer(ManagerParse);

}

function ManagerParse(response) {
    var responseObj = JSON.parse(response);
    var product = '';
	product= responseObj.model;

    alert("product =" + product);
    if(product !='') {
        g_form.setValue('product', product);
    }
}
